import 'react-app-polyfill/ie11';
import 'react-app-polyfill/stable';

import React from "react";
import { Card, CardBody, Row, Col } from "reactstrap";
//import { Link } from 'react-router-dom';

// core components_chart 입력하는 부분_
// 아래에 차트 이름과, 차트 경로를 입력하면 된다.
import chartData from "../../variables/charts.js";
import ReactFC from 'react-fusioncharts';
import ReactFusioncharts from "react-fusioncharts";
import FusionCharts from 'fusioncharts';
import charts from 'fusioncharts/fusioncharts.charts';
import Zoomline from "fusioncharts/fusioncharts.zoomline";
import FusionTheme from 'fusioncharts/themes/fusioncharts.theme.fusion';

ReactFC.fcRoot(FusionCharts, charts, Zoomline, FusionTheme);
charts(FusionCharts);

let dataSource = [];
let url = [
  "http://bcmon.kmu.ac.kr/bitcoin/ethereum/data/difficulty/summation/month",
  "http://bcmon.kmu.ac.kr/bitcoin/ethereum/data/difficulty/average/month",
];
var tag = [
  "value",
  "value",
];
var title = [
  "The total of difficulty per day",
  "The average of difficulty per day",
];
var caption = [];


class ethereumNetwork extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      bigChartData: "data1"
    };
  }
  setBgChartData = name => {
    this.setState({
      bigChartData: name

    });
  };


  render() {
    for(var i = 0; i < url.length; i++)
      {
        dataSource[i] = chartData.renderGraph(url[i], tag[i], title[i], caption[i], i);
        console.log("임시 데이터 1 - " + i + " : ");
        console.log(JSON.stringify(dataSource[i]));
      }

    return (
      <>
        <div className="content">
        <h2 className="text-center">Ethereum Network</h2>
        <p className="category text-center">
          .
        </p>

          <div className="content">
            <Row>
              <Col lg="4">
                <Card className="card-chart2" >
                  <CardBody>
                    <div className="chart-area">
                    <ReactFusioncharts
                          type="line"
                          className = "mychart"
                          width="100%"
                          height="300"
                          dataFormat="JSON"
                          dataSource={dataSource[0]}
                        />
                    </div>
                  </CardBody>
                </Card>
              </Col>
              <Col lg="4">
                <Card className="card-chart2" >
                  <CardBody>
                    <div className="chart-area">
                    <ReactFusioncharts
                          type="line"
                          className = "mychart"
                          width="100%"
                          height="300"
                          dataFormat="JSON"
                          dataSource={dataSource[1]}
                        />
                    </div>
                  </CardBody>
                </Card>
              </Col>
            </Row>
          </div>
        </div>
      </>
    );
  }
}

export default ethereumNetwork;
