import 'react-app-polyfill/ie11';
import 'react-app-polyfill/stable';

import React from "react";
import { Card, CardBody, Row, Col } from "reactstrap";
import { Link } from 'react-router-dom';

// core components_chart 입력하는 부분_
// 아래에 차트 이름과, 차트 경로를 입력하면 된다.
import chartData from "../../variables/charts.js";
import ReactFC from 'react-fusioncharts';
import ReactFusioncharts from "react-fusioncharts";
import FusionCharts from 'fusioncharts';
import charts from 'fusioncharts/fusioncharts.charts';
import Zoomline from "fusioncharts/fusioncharts.zoomline";
import FusionTheme from 'fusioncharts/themes/fusioncharts.theme.fusion';

ReactFC.fcRoot(FusionCharts, charts, Zoomline, FusionTheme);
charts(FusionCharts);

let dataSource = new Array();
let url = [
];
var tag = [
];
var title = [
];
var caption = [
];


class hyperledgerBlock extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bigChartData: "data1"
    };
  }
  setBgChartData = name => {
    this.setState({
      bigChartData: name
    });
  };

  render() {
    for(var i = 0; i < url.length; i++)
    {
      dataSource[i] = chartData.renderGraph(url[i], tag[i], title[i], caption[i], i);
      console.log("임시 데이터 1 - " + i + " : ");
      console.log(JSON.stringify(dataSource[i]));
    }
    return (
      <>
        <div className="content">
        <h2 className="text-center">Hyperledger Block</h2>
        <p className="category text-center">
          .
        </p>

          <div className="content">
            <Row>
            <Col lg="4">
              <Card className="card-chart2">
              <Link to={"/admin/bitcoin/block/blocksize/sum"}>
                <CardBody>
                  <div className="chart-area">
                  <ReactFusioncharts
                        type="line"
                        className = "mychart"
                        width="100%"
                        height="300"
                        dataFormat="JSON"
                        dataSource={dataSource[0]}
                      />
                  </div>
                </CardBody>
                </Link>
              </Card>
            </Col>
            <Col lg="4">
              <Card className="card-chart2">
              <Link to={"/admin/bitcoin/block/blocksize/average"}>
                <CardBody>
                <div className="chart-area">
                <ReactFusioncharts
                      type="line"
                      className = "fc-line"
                      width="100%"
                      height="300"
                      dataFormat="JSON"
                      dataSource={dataSource[1]}
                    />
                </div>
                </CardBody>
                </Link>
              </Card>
            </Col>
            <Col lg="4">
              <Card className="card-chart2">
              <Link to={"/admin/bitcoin/block/blocksize/total"}>
                <CardBody>
                <div className="chart-area">
                <ReactFusioncharts
                      type="line"
                      className = "fc-line"
                      width="100%"
                      height="300"
                      dataFormat="JSON"
                      dataSource={dataSource[2]}
                    />
                </div>
                </CardBody>
                </Link>
              </Card>
            </Col>
            <Col lg="4">
              <Card className="card-chart2">
              <Link to={"/admin/bitcoin/block/difficulty"}>
                <CardBody>
                  <div className="chart-area">
                  <ReactFusioncharts
                        type="line"
                        className = "mychart"
                        width="100%"
                        height="300"
                        dataFormat="JSON"
                        dataSource={dataSource[3]}
                      />
                  </div>
                </CardBody>
                </Link>
              </Card>
            </Col>
            <Col lg="4">
              <Card className="card-chart2">
              <Link to={"/admin/bitcoin/block/medianconfirmationtime"}>
                <CardBody>
                <div className="chart-area">
                <ReactFusioncharts
                      type="line"
                      className = "fc-line"
                      width="100%"
                      height="300"
                      dataFormat="JSON"
                      dataSource={dataSource[4]}
                    />
                </div>
                </CardBody>
                </Link>
              </Card>
            </Col>
            <Col lg="4">
              <Card className="card-chart2">
              <Link to={"/admin/bitcoin/block/miningRevenue"}>
                <CardBody>
                <div className="chart-area">
                <ReactFusioncharts
                      type="line"
                      className = "fc-line"
                      width="100%"
                      height="300"
                      dataFormat="JSON"
                      dataSource={dataSource[5]}
                    />
                </div>
                </CardBody>
                </Link>
              </Card>
            </Col>
            <Col lg="4">
              <Card className="card-chart2">
              <Link to={"/admin/bitcoin/block/totalrevenue"}>
                <CardBody>
                <div className="chart-area">
                <ReactFusioncharts
                      type="line"
                      className = "fc-line"
                      width="100%"
                      height="300"
                      dataFormat="JSON"
                      dataSource={dataSource[6]}
                    />
                </div>
                </CardBody>
                </Link>
              </Card>
            </Col>
            </Row>
          </div>
        </div>
      </>
    );
  }
}

export default hyperledgerBlock;
