// total data 출력시 사용
let result; //zoom in zoom out chart data

function sleep (delay) {
  var start = new Date().getTime();
  while (new Date().getTime() < start + delay);
}

//render zoom in zoom out graph
function renderZoomChart(url, tag, title, caption){
  var graph_data = new XMLHttpRequest(); // graph_data은 XMLHttpRequest 객체(http를 통해 데이터를 받음)
  graph_data.open("get", url, false); // tx합 json 데이터를 을 비동기 방식  / get 방식으로 읽어들임
  graph_data.onreadystatechange = function(){
      if(graph_data.readyState === 4){ // 통신 상태가 4 = 양호 확인
          if(graph_data.status === 200){ //status 가 200일 경우 아래 process; 로 진행
              result = process(tag, title, caption, graph_data); // 프로세스를 부르겠다는 의미 >> 아래 function process로 진행
              return result;
          }else{
                alert(title+"그래프 데이터 요청 실패:" + graph_data.status); // if의 내용이 실패시 요청 실패 : 오류 메시지 출력
          }
      }
  }
  graph_data.send(null);
  return result;
}

function process(tag, title, caption, graph_data){  // 프로세스를 부름
  var data = graph_data.responseText;          // get은 Text 방식으로
