import 'react-app-polyfill/ie11';
import 'react-app-polyfill/stable';

import React from "react";
import { Card, CardBody, Row, Col } from "reactstrap";
import { Link } from 'react-router-dom';

// core components_chart 입력하는 부분_
// 아래에 차트 이름과, 차트 경로를 입력하면 된다.
import chartData from "../../variables/charts.js";
import ReactFC from 'react-fusioncharts';
import ReactFusioncharts from "react-fusioncharts";
import FusionCharts from 'fusioncharts';
import charts from 'fusioncharts/fusioncharts.charts';
import Zoomline from "fusioncharts/fusioncharts.zoomline";
import FusionTheme from 'fusioncharts/themes/fusioncharts.theme.fusion';

ReactFC.fcRoot(FusionCharts, charts, Zoomline, FusionTheme);
charts(FusionCharts);

let dataSource = [];
let url = [
  "http://bcmon.kmu.ac.kr/coin-data/bitcoin/graphs/number-of-transaction/total/month",
  "http://bcmon.kmu.ac.kr/coin-data/bitcoin/graphs/number-of-transaction/sum/month",
  "http://bcmon.kmu.ac.kr/coin-data/bitcoin/graphs/number-of-transaction/average/month",
  "http://bcmon.kmu.ac.kr/coin-data/bitcoin/graphs/transaction/prediction-number-of-transaction/value/month"
];
var tag = [
  "value",
  "value",
  "value",
  "value"
];
var title = [
  "Cumulative",
  "Total",
  "Average",
  "Prediction to number of transaction per block"
];
var caption = [
  "The cumulative value of number of transactions per day",
  "The total number of transactions per day.",
  "The average number of transactions per day.",
  "The prediction to number of transactions of next block."
];

class bitcoinTx extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bigChartData: "data1",
      //mean은 그래프 당 의미가 하나 밖에 없을 때는 사용하지 말것.
      mean:"Sum of Tx"
    };
  }
  // 버튼 사용시 사용하는 생성자
  setBgChartData = (name1, name2) => {
    this.setState({
      bigChartData: name1,
      mean: name2
    });
  };

  render() {
  for(var i = 0; i < url.length; i++)
  {
    dataSource[i] = chartData.renderGraph(url[i], tag[i], title[i], caption[i], i);
    console.log("임시 데이터 1 - " + i + " : ");
    console.log(JSON.stringify(dataSource[i]));
  }

    return (
      <>
        <div className="content">
          <h2 className="text-center">Bitcoin Transaction</h2>

          <div className="content">
            <h4 className="title pl-6">
              <small>Number of Transaction</small>
            </h4>
            <Row>
              <Col lg="4">
                <Card className="card-chart2">
                  <Link to={"/admin/bitcoin/transaction/number-of-transaction/sum/year"}>
                    <CardBody>
                      <div className="chart-area" >
                        <ReactFusioncharts
                          type="line"
                          classNameBitcoin  = "mychart"
                          width="100%"
                          height="300"
                          dataFormat="JSON"
                          dataSource={dataSource[0]}
                        />
                      </div>
                    </CardBody>
                  </Link>
                </Card>
              </Col>
              <Col lg="4">
                <Card className="card-chart2">
                  <Link to={"/admin/bitcoin/transaction/number-of-transaction/average/year"}>
                    <CardBody>
                      <div className="chart-area">
                        <ReactFusioncharts
                          type="line"
                          className = "fc-area2d"
                          width="100%"
                          height="300"
                          dataFormat="JSON"
                          dataSource={dataSource[1]}
                        />
                      </div>
                    </CardBody>
                  </Link>
                </Card>
              </Col>
              <Col lg="4">
                <Card className="card-chart2">
                  <Link to={"/admin/bitcoin/transaction/number-of-transaction/total/year"}>
                    <CardBody>
                      <div className="chart-area" >
                        <ReactFusioncharts
                          type="line"
                          className = "mychart"
                          width="100%"
                          height="300"
                          dataFormat="JSON"
                          dataSource={dataSource[2]}
                        />
                      </div>
                    </CardBody>
                  </Link>
                </Card>
              </Col>
            </Row>
            <Row>
              <Col lg="4">
                <Card className="card-chart2">
                  <Link to={"/admin/bitcoin/transaction/prediction-number-of-transaction/value/year"}>
                    <CardBody>
                      <div className="chart-area" >
                        <ReactFusioncharts
                          type="line"
                          classNameBitcoin  = "mychart"
                          width="100%"
                          height="300"
                          dataFormat="JSON"
                          dataSource={dataSource[3]}
                        />
                      </div>
                    </CardBody>
                  </Link>
                </Card>
              </Col>
            </Row>
            <h4 className="title line pl-6">
              <small>Number of Inputs</small><hr/>
            </h4>
            <Row>
              <Col lg="4">
                <Card className="card-chart2">
                  <Link to={"/admin/bitcoin/transaction/number-of-transaction/sum/year"}>
                    <CardBody>
                      <div className="chart-area" >
                        <ReactFusioncharts
                          type="line"
                          classNameBitcoin  = "mychart"
                          width="100%"
                          height="300"
                          dataFormat="JSON"
                          dataSource={dataSource[4]}
                        />
                      </div>
                    </CardBody>
                  </Link>
                </Card>
              </Col>
              <Col lg="4">
                <Card className="card-chart2">
                  <Link to={"/admin/bitcoin/transaction/number-of-transaction/average/year"}>
                    <CardBody>
                      <div className="chart-area">
                        <ReactFusioncharts
                          type="line"
                          className = "fc-area2d"
                          width="100%"
                          height="300"
                          dataFormat="JSON"
                          dataSource={dataSource[5]}
                        />
                      </div>
                    </CardBody>
                  </Link>
                </Card>
              </Col>
            </Row>
          </div>
        </div>
      </>
    );
  }
}




export default bitcoinTx;
