import 'react-app-polyfill/ie11';
import 'react-app-polyfill/stable';

import React from "react";
import { Card, CardBody, Row, Col } from "reactstrap";
import { Link } from 'react-router-dom';

// core components_chart 입력하는 부분_
// 아래에 차트 이름과, 차트 경로를 입력하면 된다.
import chartData from "../../variables/charts.js";
import ReactFC from 'react-fusioncharts';
import ReactFusioncharts from "react-fusioncharts";
import FusionCharts from 'fusioncharts';
import charts from 'fusioncharts/fusioncharts.charts';
import Zoomline from "fusioncharts/fusioncharts.zoomline";
import FusionTheme from 'fusioncharts/themes/fusioncharts.theme.fusion';

ReactFC.fcRoot(FusionCharts, charts, Zoomline, FusionTheme);
charts(FusionCharts);

let dataSource = [];
let url = [
  "http://bcmon.kmu.ac.kr/bitcoin/graphs/node/used-buffer-size/average/month",
  "http://bcmon.kmu.ac.kr/bitcoin/graphs/node/used-cached-size/average/month",
  "http://bcmon.kmu.ac.kr/bitcoin/graphs/node/used-cpu/average/month",
  "http://bcmon.kmu.ac.kr/bitcoin/graphs/node/used-memory/average/month",
  "http://bcmon.kmu.ac.kr/bitcoin/graphs/node/used-verificationprogres/average/month"
];
var tag = [
  "value",
  "value",
  "value",
  "value",
  "value"
];
var title = [
  "Buffer Size ",
  "Cached Size ",
  "CPU Usage",
  "Memory Usage",
  "Verification Progress"
];
var caption = [
  "Node used Average Buffer Size per day",
  "Node used Average Cached Size per day",
  "Node used Average CPU percentage per day",
  "Node used Average Memory percentage per day",
  "Node used Average Verification Progress per day"
];

class bitcoinNode extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bigChartData: "data1"
    };
  }
  setBgChartData = name => {
    this.setState({
      bigChartData: name
    });
  };

  render() {
    for(var i = 0; i < url.length; i++)
    {
      dataSource[i] = chartData.renderGraph(url[i], tag[i], title[i], caption[i], i);
      console.log("임시 데이터 1 - " + i + " : ");
      console.log(JSON.stringify(dataSource[i]));
    }

    return (
      <>
        <div className="content">
        <h2 className="text-center">Bitcoin Node</h2>
        <p className="category text-center">
          .
        </p>
          <div className="content">
            <Row>
              <Col lg="4">
                <Card className="card-chart2">
                <Link to={"/admin/bitcoin/node/used-buffer-size/average/month"}>
                  <CardBody>
                    <div className="chart-area" >
                      <ReactFusioncharts
                          type="line"
                          className = "mychart"
                          width="100%"
                          height="300"
                          dataFormat="JSON"
                          dataSource={dataSource[0]}
                        />
                    </div>
                  </CardBody>
                  </Link>
                </Card>
              </Col>
              <Col lg="4">
                <Card className="card-chart2">
                <Link to={"/admin/bitcoin/node/CachedSize"}>
                  <CardBody>
                    <div className="chart-area" >
                      <ReactFusioncharts
                          type="line"
                          className = "mychart"
                          width="100%"
                          height="300"
                          dataFormat="JSON"
                          dataSource={dataSource[1]}
                        />
                    </div>
                  </CardBody>
                  </Link>
                </Card>
              </Col>
              <Col lg="4">
                <Card className="card-chart2">
                <Link to={"/admin/bitcoin/node/CPU"}>
                  <CardBody>
                    <div className="chart-area" >
                      <ReactFusioncharts
                          type="line"
                          className = "mychart"
                          width="100%"
                          height="300"
                          dataFormat="JSON"
                          dataSource={dataSource[2]}
                        />
                    </div>
                  </CardBody>
                  </Link>
                </Card>
              </Col>
            </Row>
            <Row>
              <Col lg="4">
                <Card className="card-chart2">
                <Link to={"/admin/bitcoin/node/avgUsedMemory"}>
                  <CardBody>
                    <div className="chart-area" >
                      <ReactFusioncharts
                          type="line"
                          className = "mychart"
                          width="100%"
                          height="300"
                          dataFormat="JSON"
                          dataSource={dataSource[3]}
                        />
                    </div>
                  </CardBody>
                  </Link>
                </Card>
              </Col>
              <Col lg="4">
                <Card className="card-chart2">
                <Link to={"/admin/bitcoin/node/avgUsedVerificationprogres"}>
                  <CardBody>
                    <div className="chart-area" >
                      <ReactFusioncharts
                          type="line"
                          className = "mychart"
                          width="100%"
                          height="300"
                          dataFormat="JSON"
                          dataSource={dataSource[4]}
                        />
                    </div>
                  </CardBody>
                  </Link>
                </Card>
              </Col>
            </Row>
          </div>
        </div>
      </>
    );
  }
}

export default bitcoinNode;
