let result = []

function sleep (delay) {
  var start = new Date().getTime();
  while (new Date().getTime() < start + delay);
}

// datainfo = [{'url', 'call_count', 'tag', 'title', 'caption', 'InValue', 'OutValue'}]
function renderRealTimeData(dataInfo){
  var get_data = new XMLHttpRequest();       // get_data은 XMLHttpRequest 객체(http를 통해 데이터를 받음)
  var url = dataInfo['url']

  get_data.open("get", url, false); // tx합 json 데이터를 을 비동기 방식  / get 방식으로 읽어들임
  get_data.onreadystatechange = function(){
      if(get_data.readyState === 4){       // 통신 상태가 4 = 양호 확인
          if(get_data.status === 200){     //status 가 200일 경우 아래 process; 로 진행
              result = process(dataInfo, get_data); // 프로세스를 부르겠다는 의미 >> 아래 function process로 진행
              return result;
          }else{
              var title = url = dataInfo['title']
              alert(title+"그래프 데이터 요청 실패:" + get_data.status); // if의 내용이 실패시 요청 실패 : 오류 메시지 출력
          }
      }
  }
  get_data.send(null);
  return result;
}

function process(dataInfo, get_data){
  var data = get_data.responseText;          // get은 Text 방식으로
  var parseData = JSON.parse(data);     //객체화 : 배열 처리

}
