// total data 출력시 사용
let result; //zoom in zoom out chart data

function sleep (delay) {
  var start = new Date().getTime();
  while (new Date().getTime() < start + delay);
}

//render zoom in zoom out graph
function renderZoomChart(url, tag, title, caption){
  var graph_data = new XMLHttpRequest(); // graph_data은 XMLHttpRequest 객체(http를 통해 데이터를 받음)
  graph_data.open("get", url, false); // tx합 json 데이터를 을 비동기 방식  / get 방식으로 읽어들임
  graph_data.onreadystatechange = function(){
      if(graph_data.readyState === 4){ // 통신 상태가 4 = 양호 확인
          if(graph_data.status === 200){ //status 가 200일 경우 아래 process; 로 진행
              result = process(tag, title, caption, graph_data); // 프로세스를 부르겠다는 의미 >> 아래 function process로 진행
              return result;
          }else{
                alert(title+"그래프 데이터 요청 실패:" + graph_data.status); // if의 내용이 실패시 요청 실패 : 오류 메시지 출력
          }
      }
  }
  graph_data.send(null);
  return result;
}

function process(tag, title, caption, graph_data){  // 프로세스를 부름
  var data = graph_data.responseText;          // get은 Text 방식으로
  var dataParse = JSON.parse(data);     //객체화 : 배열 처리

  var datasource = {};
  var data_category = [];
  var data_value = [];

  console.log(dataParse);
  console.log(dataParse);
  for (var index = 0; index < dataParse.length; index++){
    data_category.push({'label': dataParse[index]._id})
    data_value.push({'value': dataParse[index][tag]})
  }

  datasource = {
    chart: {
      caption: title,
      subcaption: caption,
      xaxisname: "Block Height",
      yaxisname: "Propagation Time(sec)",
      "rotatelabels": "1",
      "drawAnchors": "0",
      "showLabels": "0",
      "palettecolors": "#17afb7",       // 선 색상 변경 가능
      "setadaptiveymin": "1",
      "linethickness": "3",
      "forceaxislimits": "1",
      "pixelsperpoint": "0",
      "pixelsperlabel": "30",
      "compactdatamode": "1",
      "theme": "fusion",
      aligncaptionwithcanvas: "0",

      "numberscalevalue": "1000,1000,1000,1000,1000",
      "numberscaleunit": "K,M,G,T,P",

      "showBorder": "0",
      "showShadow": "0",
      //use3DLighting: "0",
      "bgColor": "#25263b",
      //bgAlpha: "0",
      "canvasBgAlpha": "0",
      "plotBorderAlpha": "0",

      "toolTipBgcolor": "#484E69",
      "tooltipBorderThickness": "0.7",
      "toolTipColor": "#FDFDFD",
      "baseFont": "",
      "baseFontSize": "10",
      "baseFontColor": "ededf0",

      "showLegend": "1",
      "legendShadow": "0",
      "legendBorderAlpha": "0",
      "drawCustomLegendIcon": "1",
      "legendBgAlpha": "0",

      "showAlternateHGridColor": "0",

      "legendNumColumns": "1",
      "legendPosition": "RIGHT",
      "defaultCenterLabel": "Total <br> $214",
      "centerLabel": "$label<br>$value",
      "centerLabelBold": "2",
      "centerLabelFontSize": "20",
      "enableRotation": "0",
      "transposeAnimation": "1",
      "plotToolText": "<b>$dataValue</b> leads received"
    },
    categories:[
      {
        category: data_category
      }
    ],
    dataset:[
      {
        seriesname: "block time",
        data: data_value
    }
  ]
  };

return datasource;
}

module.exports = {
  sleep,
  renderZoomChart,
};
