import 'react-app-polyfill/ie9';
import 'react-app-polyfill/ie11';
import 'react-app-polyfill/stable';

import React from "react";
import { VectorMap } from "react-jvectormap";
import { Card, CardHeader, CardTitle, CardBody, Table, Row, Col } from "reactstrap";
import { Link } from 'react-router-dom';
import { hasFlag } from 'country-flag-icons'
import chartData from "../../variables/chartData.js";
import ReactCountryFlag from "react-country-flag"

let dataSource = 0;
let ethereum_node_discovery_url = "http://bcmon.kmu.ac.kr/coin-data/ethereum/view/node/discovery/latest/results";
var mapData = 0;

class ethereumBlock extends React.Component {
  render() {
    //chartData.renderTableData(ethereum_node_discovery_url, 5);
    dataSource = chartData.renderTableData(ethereum_node_discovery_url, 5);
    mapData = dataSource[3];

    const listItems = dataSource[1].map((list_) =>
      <tr>
        <td>
          <ReactCountryFlag
              countryCode={list_.country_code}
              svg
              cdnUrl="https://cdnjs.cloudflare.com/ajax/libs/flag-icon-css/3.4.3/flags/1x1/"
              cdnSuffix="svg"
              title={list_.country_code}
            />
        </td>
        <td>{list_.country_name}</td>
        <td>{list_.node_count}</td>
        <td>{list_.ratio}</td>
      </tr>
    );

    return (
      <>
        <div className="content">
        <h2 className="text-center">Management</h2>
          <div className="title content">
            <Row>
              <Col sm="12" lg="4" md="4">
                <Card className="card-management-firstoutline">
                  <CardHeader>
                    <CardTitle>
                      <h3>Bitcoin Network Topology</h3>
                    </CardTitle>
                  </CardHeader>
                  <Row>
                  <Col xs={12} sm={3} md={2} lg={1}></Col>
                  <Col xs={6} sm={6} md={8} lg={10}>
                      <Card className="card-management-tableColor" >
                        <Link to={"/admin/advanced_analysis/management/topology"}>
                          <CardBody className="text-center">
                            <CardTitle><h4>Bitcoin Network Topology Statistics</h4></CardTitle>
                            <Table responsive>
                              <thead className="text-primary">
                                <tr>
                                  <th className="text-center" >Avg path length</th>
                                  <th className="text-center">22.532</th>
                                </tr>
                                <tr>
                                  <th className="text-center" >Network Centralization</th>
                                  <th className="text-center">0.058</th>
                                </tr>
                                <tr>
                                  <th className="text-center" >Avg Clustering Coefficient</th>
                                  <th className="text-center">0.082</th>
                                </tr>
                              </thead>
                            </Table>
                          </CardBody>
                        </Link>
                      </Card>
                    </Col>
                    <Col xs={6} sm={3} md={2} lg={1}></Col>
                    </Row>
                </Card>
                </Col>
            </Row>
            <Row>
              <Col>
                <Card className="card-management-evenoutline">
                  <CardHeader>
                    <CardTitle>
                        <h3>Ethereum Node Discovery</h3>
                        <p>BcMon is currently being developed to find all the reachable nodes in the Ethereum network.</p>
                      </CardTitle>
                  </CardHeader>
                  <Link to={"/admin/advanced_analysis/management/ethereum_node_discovery"}>
                    <CardBody>
                      <Row>
                        <Col md="6">
                          <Table responsive>
                            <tbody>
                              <tr>
                                {listItems}
                              </tr>
                            </tbody>
                          </Table>
                        </Col>
                        <Col className="ml-auto mr-auto" md="6">
                          <VectorMap
                            map={"world_mill"}
                            backgroundColor="transparent"
                            zoomOnScroll={true}
                            containerStyle={{
                              width: "100%",
                              height: "280px"
                            }}
                            regionStyle={{
                              initial: {
                                fill: "#e4e4e4",
                                "fill-opacity": 0.9,
                                stroke: "none",
                                "stroke-width": 0,
                                "stroke-opacity": 0
                              }
                            }}
                            series={{
                              regions: [
                                {
                                  values: mapData,
                                  scale: ["#AAAAAA", "#444444"],
                                  normalizeFunction: "polynomial"
                                }
                              ]
                            }}
                          />
                        </Col>
                      </Row>
                    </CardBody>
                  </Link>
                </Card>
              </Col>
            </Row>
          </div>
        </div>
      </>
    );
  }
}

export default ethereumBlock;
