    "plotBorderAlpha": "0",

    "toolTipBgcolor": "#484E69",
    //toolTipPadding: "7",
    "tooltipBorderThickness": "0.7",
    "toolTipColor": "#FDFDFD",
    "baseFont": "",
    "baseFontSize": "10",
    "baseFontColor": "#FDFDFD",

    "showLegend": "1",
    "legendShadow": "0",
    "legendBorderAlpha": "0",
    "drawCustomLegendIcon": "1",
    "legendBgAlpha": "0",

    "showAlternateHGridColor": "0",
